DROP TABLE IF EXISTS osm_states;
CREATE TABLE osm_states (
    osm_id integer,
    abbreviation varchar(10),
    area integer,
    name varchar(30),
    name_en varchar(30),
    name_de varchar(30),
    name_fr varchar(30),
    name_es varchar(30),
    name_ru varchar(30),
    name_zh varchar(30),
    geometry geometry,
    id integer primary key
);
SELECT UpdateGeometrySRID('osm_states', 'geometry', 4326);

